'use strict';

const test = require('ava');
const greet = require('../index');

test('Greeting', t => {
   const greeting = greet("Tom");
   t.is(greeting, "Hello, Tom.");
});

test('Greeting handles null', t => {
   const greeting = greet();
   t.is(greeting, "Hello, my friend.");
});
